// Extract text safely
const data = $json.content?.parts?.[0]?.text || $json.text || $json.output;

if (!data) {
  throw new Error("No text found. Actual input:\n" + JSON.stringify($json, null, 2));
}

// Remove ```json ... ``` wrappers
const cleaned = data.replace(/```json\n?/, '').replace(/```$/, '').trim();

// Parse into object
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (err) {
  throw new Error("Failed to parse JSON: " + err.message + "\nInput was:\n" + cleaned);
}

// Wrap everything into "slides"
return [{ slides: parsed }];
